import { Component, NgModule, OnInit } from '@angular/core';
import { Region } from '../model/region';
import { CountryStatsFilterService } from '../service/country-stats-filter.service';
import { CountryStatsFilter } from '../model/country-stats-filter';
import { FormsModule } from '@angular/forms';

@Component({
  selector: 'app-country-stats-filter',
  templateUrl: './country-stats-filter.component.html',
  styleUrls: ['./country-stats-filter.component.css']
})
export class CountryStatsFilterComponent implements OnInit {

  regions: Region[]; 
  countryStats: CountryStatsFilter[]; 
  filteredData: CountryStatsFilter[]; 
  selectedRegion: string = '';
  dateFrom: number;
  dateTo: number;

  constructor(private countryStatsFilterService: CountryStatsFilterService) { }

  ngOnInit(): void {
    this.countryStatsFilterService.getRegions().subscribe(data => {
      this.regions = data;
    });
    this.countryStatsFilterService.getCountryStatistics().subscribe(data => {
      this.countryStats = data;
    });
    this.filteredData = this.countryStats; 
  }

  filterData(): void {
    this.filteredData = this.countryStats.filter(country => {
      return (
        (this.selectedRegion === '' || country.region === this.selectedRegion) &&
        (!this.dateFrom || country.year >= this.dateFrom) &&
        (!this.dateTo || country.year <= this.dateTo)
      );
    });
  }

}
<div class="filter-container">
    <label for="regionFilter">Select Region:</label>
    <select id="regionFilter" [(ngModel)]="selectedRegion" (ngModelChange)="filterData()">
      <option value="">All Regions</option>
      <option *ngFor="let region of regions" [value]="region.name">{{ region.name }}</option>
    </select>
    <label for="dateFrom">From:</label>
    <input type="number" id="dateFrom" [(ngModel)]="dateFrom" (input)="filterData()">
    <label for="dateTo">To:</label>
    <input type="number" id="dateTo" [(ngModel)]="dateTo" (input)="filterData()">
  </div>

  <div class="table-container">
    <table class="country-stats-table">
      <thead>
        <tr>
          <th>Continent</th>
          <th>Region</th>
          <th>Country</th>
          <th>Year</th>
          <th>Population</th>
          <th>GDP</th>
        </tr>
      </thead>
      <tbody>
        <tr *ngFor="let countryStats of filteredData">
          <td>{{ countryStats.continent }}</td>
          <td>{{ countryStats.region }}</td>
          <td>{{ countryStats.country }}</td>
          <td>{{ countryStats.year }}</td>
          <td>{{ countryStats.population }}</td>
          <td>{{ countryStats.gdp }}</td>
        </tr>
      </tbody>
    </table>
  </div>
