import { Injectable } from '@angular/core';
import { HttpClient, HttpHeaders } from '@angular/common/http';
import { Language } from '../model/language';
import { Observable } from  'rxjs';
import { ActivatedRoute, ParamMap } from '@angular/router'


@Injectable({
  providedIn: 'root'
})
export class LanguageService {

  private languagesUrl: string;
  private country_id: number;

  constructor(private http: HttpClient, private activatedRoute: ActivatedRoute) {
    this.activatedRoute.paramMap.subscribe((params: ParamMap) => {
      this.country_id = +params.get('id')!
    });
    this.languagesUrl = 'http://localhost:8080/languages/' + 1;
  }


  public getLanguagesByCountryId(country_id: number): Observable<Language[]> {

    return this.http.get<Language[]>(this.languagesUrl);
  }
}
